mapper.select:
 select * from t_user 
 
	 
  	username = #{username}
	
	
  user_id = #{userId}
	
	
  phone = #{phone}
	
	
  qq = #{qq}
	
	
  weixin = #{weixin}
	
	
  weibo = #{weibo}

mapper.update:
update t_user 

	
	
  	
    	password = #{newPassword}
  	
  	
    	icon_url = #{iconUrl}
  	
  	
    	nickname = #{nickname}
  	
  	
    	birth_date = #{birthDate}
  	
  	
    	sex = #{sex}
  	
  	
    	phone = #{newPhone}
  	

   where  user_id = #{userId}
